function [pressed, time] = checkTarPress(targets)% Checks for button presses specified in targets, which may be N multiple keys% Specify different targets in different entries of a string array% Returns whether buttons were pressed (1 to N if true, 0 otherwise)%% 2008 by Martin Rolfs[~,t,keyCode] = KbCheck(-3); %input argument -1 will query all keyboardspressed = [];key = find(keyCode==1);if length(key) == 1	pressed = find(targets==key);    time    = t;endif isempty(pressed)	pressed = 0;    time    = [];end	